record.field:
taxes_id = env['account.tax'].search([('nxt_id_erp','=',record["taxes_id"])])
record["taxes_id"] = [taxes_id.id]

json_data = {}
rec = env['product.product'].search([('nxt_id_erp','=',record["nxt_id_erp"])])
if rec :
    result = rec.write(record)
    post_type = "write"
    _logger.error("Update articulo !  item_id:" + str(result) + " - " + str(record))
else:
    result = env['product.product'].create(record)
    post_type = "create"
    _logger.error("Create articulo !  item_id:" + str(result) + " - " + str(record))

json_data = {
    "condition" : post_type,
    "record" : record
}

pricelist = None #SET LISTA DE PRECIO PADRE for row in record: pricelist = env['product.pricelist'].search([('nxt_id_erp','=',row["nxt_id_erp"])], limit=1) break
#ELIMINA LINEA DE LISTAS DE PRECIOS pricelist_item = env['product.pricelist.item'].search([('pricelist_id','=',pricelist.id)]) for price_line in pricelist_item : price_line.unlink();
#CREA LINEA DE LISTAS DE PRECIOS for row in record: pricelist_item = env['product.template'].search([('nxt_id_erp','=',row["product_tmpl_id"])]) if pricelist_item: del row["nxt_id_erp"] row["applied_on"] = "1_product" row["compute_price"] = "fixed" row["pricelist_id"] = pricelist.id row["product_tmpl_id"] = pricelist_item.product_variant_id.id result = env['product.pricelist.item'].create(row) post_type = "create" _logger.error("Create linea de listas de precios ! item_id:" + str(result) + " - " + str(row))
json_data = { "condition" : post_type, "record" : record }
